import './App.css';
import './normal.css'
import { useState } from 'react';
import { Configuration, OpenAIApi } from 'openai';
import { inject } from '@vercel/analytics';

inject();

const orgId = "org-9HfRDuLSYdMqot8sxBpkd5A0"
const apiKey = "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"
const eodApi = "63a2477acc2587.58203009"

function App() {


  // state hooks
  const [stockName, setStockname] = useState("");
  const [input, setInput] = useState("");
  const [chatLog, setChatLog] = useState([])
  const [showOverlay, setShowOverlay] = useState(true);
  const [interval, setInterval] = useState("5m");
  const [from, setFrom] = useState("");
  const [to, setTo] = useState("");


  // openAI auth
  const configuration = new Configuration({
    orgId: orgId,
    apiKey: apiKey,
  });
  const openai = new OpenAIApi(configuration);

  // main prompter, runs off context
  async function getCompletion(message) {
    const response = await openai.createCompletion({
      model: "text-davinci-003",
      prompt: `"You are an artificial financial & investment advisor named Kari. When a user has a response that is non-conversational, simply affirmative, or you don't quite know how to respond to, just talk about your abilities again.  Don't correct the user's punctuation if they're lacking it. Ask one question at a time when lacking helpful context. Here is the prompt: ${message}"`,
      max_tokens: 500,
      temperature: .7,
      stop: "/n",
    });
    return response.data.choices[0].text;
  }

    // prompter that standardizes the stock queries into normalized inputs
      async function standardizeInput() {
        const evaluation = await openai.createCompletion({
            model: "text-davinci-002",
            prompt: `Is this input related to a live stock price? "${input}"`,
            max_tokens: 50,
            temperature: .5,
            stop: "/n",
        });
        const evaluationText = evaluation.data.choices[0].text.toLowerCase();
        let standardizedInput; 
        if (evaluationText.includes("yes")) {
            const reformattedInput = await openai.createCompletion({
                model: "text-davinci-002",
                prompt: `Reformat this input so it can be read by my script that can match against a pattern. The desired format is: "Get me stock information for [STOCK] from [FROM-DATE] to [TO-DATE] at [1-minute-intervals, 5-MINUTE-INTERVALS, 1-HOUR-INTERVALS]. "${input}"`,
                max_tokens: 500,
                temperature: .5,
                stop: "/n",
            });
            standardizedInput = reformattedInput.data.choices[0].text;
        } else {
            standardizedInput = input;
        }
        return standardizedInput; // Return the standardized input
    }

  // prompter that takes in stock info
  async function analyzeData(data, instructions) {
    const response = await openai.createCompletion({
      model: "text-curie-001",
      prompt: `Make sure to round decimals to 2 places thanks babe. ${input} ${stockName} ${from} ${to} ${interval} ${JSON.stringify(data)}"`,
      max_tokens: 2048,
      temperature: .5,
      stop: "/n",
    });
    return response.data.choices[0].text;
  }
// this should be the function that primes the bot with necessary context It will set. Then we will create another function that gets a response from the bot based on the context it already knows.
async function handleSubmit(e) {
  e.preventDefault();
    let chatLogNew = [...chatLog];

    setInput("");
    setStockname("");
    setTo("");
    setFrom("");
    setInterval("");
    setShowOverlay(false);

    const standardizedInput = await standardizeInput();
    const pattern = /(?<stockName>\b[A-Z]+\b).*(?<fromDate>\b\d{2}\/\d{2}\/\d{4}\b).*(?<toDate>\b\d{2}\/\d{2}\/\d{4}\b).*(?<interval>\b\d{1}-minute\b)/;
    const match = standardInput.match(pattern);

    const stockName = match.groups.stockName;
    const from = match.groups.fromDate;
    const to = match.groups.toDate;
    const interval = match.groups.interval;

    console.log(stockName); // TSLA
    console.log(from); // 01/01/2021
    console.log(to); // 05/31/2021
    console.log(interval); // 5-minute

    if (input.trim() && input.length > 4) {
      chatLogNew.push({ user: "me", message: `${input}`});
      const messages = chatLogNew.map((message) => message.message).join("");
      const data = await getCompletion(messages);
      chatLogNew.push({ user: "gpt", message: `${data}`});
    }
    setChatLog(chatLogNew);
}

  function clearChat(){
    setChatLog([]);
    setShowOverlay(true);
  }

  return (
    <div className="App">
<aside className="sidemenu"> 
  <h3 className="sidemenu-header">Kari</h3>
   <button className="side-menu-button" onClick={clearChat}> Clear Chat </button> 
   <button className="side-menu-button" onClick={() => window.open("https://forms.gle/6rQ4nsWddMqPgsCw9")}> Sign Up for Waitlist </button> 
   <button className="side-menu-button" onClick={() => window.open("src/components/stockAnalysis.js")}> Stock Analysis Tool </button> </aside>
      <section className="chatbox">
      {showOverlay && (
          <div className="overlay-card">
            <div>
              <h2>Kari.ai Financial Advisor</h2>
            </div>
          <div className="overlay-card-columns">
            <div className="overlay-card-column-example">
              <h3>Example Prompts:</h3>
              <p onClick={() => setInput("What are the future trends in technology, and who are the biggest players?")}>"What are the future trends in technology, and who are the biggest players?"</p>
              <p onClick={() => setInput("What are the best investment options for someone in their 20s with a moderate level of risk tolerance?")}>"What are the best investment options for someone in their 20s with a moderate level of risk tolerance?"</p>
              <p onClick={() => setInput("What is your name and how can you actually help me with my finances?")}>"What is your name and how can you actually help me with my finances?"</p>
            </div>
            <div className="overlay-card-column">
              <h3>Current Limitations:</h3>
              <p>The chat may not have access to the most recent data on financial topics.</p>
              <p>The chat does not have access to the user's personal financial situation or other relevant context.</p>
              <p>The chat's answers may be limited by the quality and clarity of the prompts it receives.</p>
            </div>
            <div className="overlay-card-column">
              <h3>Future Features:</h3>
              <p>Save Threads</p>
              <p>Access to Live Data</p>
              <p>Charting & Data Visualization</p>
              <p>And more!</p>
            </div>
          </div>
        </div>        
        )}
        <div className="chat-log">
          {chatLog.map((message, index) => (
            <ChatMessage key={index} message={message} />
          ))}
    </div>
    <div className="chat-input-holder">
  <div className="stock-screening-tool">
    <form onSubmit={handleSubmit}>
      <input 
        rows="1"
        value={stockName}
        onChange={(e) => setStockname(e.target.value)}
        className="chat-input-textarea"
        placeholder="Enter Stock Name"
      />
      <input 
        placeholder="From: dd/mm/yyyy"
        value={from}
        onChange={e => setFrom(e.target.value)}
      />
      <input
        placeholder="To: dd/mm/yyyy"
        value={to}
        onChange={e => setTo(e.target.value)}
      />
      <select value={interval} onChange={e => setInterval(e.target.value)}>
        <option value="1m">1-minute</option>
        <option value="5m">5-minute</option>
        <option value="1h">1-hour</option>
      </select>
      <input 
        rows="1"
        value={input}
        onChange={(e) => setInput(e.target.value)}
        className="chat-input-textarea"
        placeholder="Ask a question or give a command"
      />
      <button className="submit-button" onClick={() => {handleSubmit();}}>Submit</button>
    </form>
  </div>
