import './App.css';
import './normal.css'
import { useState } from 'react';
import { Configuration, OpenAIApi } from 'openai';

const orgId = "org-9HfRDuLSYdMqot8sxBpkd5A0"
const apiKey = "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"

function App() {

  const configuration = new Configuration({
    organization: orgId,
    apiKey: apiKey,
  });
  const openai = new OpenAIApi(configuration);

  async function getCompletion(message) {
    const response = await openai.createCompletion({
      model: "text-davinci-003",
      prompt: `${message}`,
      max_tokens: 300,
      temperature: 0.5,
    });
    return response.data.choices[0].text;
  }

  const [input, setInput] = useState("");
  const [chatLog, setChatLog] = useState([])
  const [showOverlay, setShowOverlay] = useState(true);

  function clearChat(){
    setChatLog([]);
    setShowOverlay(true);
  }

  async function handleSubmit(e) {
    e.preventDefault();
    let chatLogNew = [...chatLog, { user: "me", message: `${input}`}]
    setInput("");
    setChatLog(chatLogNew);
    setShowOverlay(false);

    const messages = chatLogNew.map((message) => message.message).join("")

    const data = await getCompletion(messages);

    // Split the response into an array of words
    const words = data.split(" ");
    // Initialize a counter variable
    let i = 0;
    // Set an interval to add one word to the chat log every 1000 milliseconds
    const interval = setInterval(() => {
      // Add the next word to the chat log, concatenating it with the previous message
      setChatLog([...chatLogNew, { user: "gpt", message: `${chatLogNew[chatLogNew.length - 1].message} ${words[i]}`}])
      // Increment the counter
      i++;
      // If all words have been added, clear the interval
      if (i >= words.length) {
        clearInterval(interval);
      }
    }, 10);
  }





  return (
    <div className="App">
      <aside className="sidemenu">
      <h3 className="sidemenu-header">Kari.ai</h3>
        <button className="side-menu-button" onClick={clearChat}>
          <span>+</span>
          New Chat
        </button>
      </aside>
      <section className="chatbox">
      {showOverlay && (
          <div className="overlay-card">
            <div>
              <h3>Kari.AI</h3>
            </div>
          <div className="overlay-card-columns">
            <div className="overlay-card-column-example">
              <h3>Examples:</h3>
              <p onClick={() => setInput("What is the current market trend for AAPL stock?")}>"What is the current market trend for AAPL stock?"</p>
              <p onClick={() => setInput("What are the best investment options for someone in their 20s with a moderate level of risk tolerance?")}>"What are the best investment options for someone in their 20s with a moderate level of risk tolerance?"</p>
              <p onClick={() => setInput("How can I create a budget plan to save for a down payment on a house?")}>"How can I create a budget plan to save for a down payment on a house?"</p>
            </div>
            <div className="overlay-card-column">
              <h3>Current Limitations:</h3>
              <p>The chat may not have access to the most recent data on financial topics.</p>
              <p>The chat does not have access to the user's personal financial situation or other relevant context.</p>
              <p>The chat's answers may be limited by the quality and clarity of the prompts it receives.</p>
            </div>
            <div className="overlay-card-column">
              <h3>Future Features:</h3>
              <p>Save Threads</p>
              <p>Access to Live Data</p>
              <p>Charting & Data Visualization</p>
              <p>And more!</p>
            </div>
          </div>
        </div>        
        )}
        <div className="chat-log">
          {chatLog.map((message, index) => (
            <ChatMessage key={index} message={message} />
          ))}
    </div>
      <div className="chat-input-holder">

        <form onSubmit={handleSubmit}>
        <input 
        rows="1"
        value={input}
        onChange={(e) => setInput(e.target.value)}
        className="chat-input-textarea"></input>
        </form>
        <p className='below-chatbox'>January 4 version. At this stage, feedback is very crucial. If you are beta-testing, please fill out <a href="https://forms.gle/YvjMHj8kPX7xDX2H8" target="_blank">this form</a> and run through the feedback questions as it will help me a lot.</p>      </div>
      </section>
    </div>
  );
}


const ChatMessage = ({ message }) => {
  return (
    <div className={`chat-message ${message.user === "gpt" && "chatgpt"}`}>
    <div className="chat-message-center">
    <div className={`avatar ${message.user === "gpt" && "chatgpt"}`}>
      {message.user === "gpt" && <svg></svg>}
    </div> 
    <div className="message">
      {message.message}
  </div>
</div>
</div>
  )
}
export default App;
